mapper.insert:
INSERT INTO scenarios (
    video_id, scenario_json, opening_prompt, negative_prompt,
    character_block, timed_segments, version
)
VALUES (
    #{videoId}, #{scenarioJson}, #{openingPrompt}, #{negativePrompt},
    #{characterBlock}, #{timedSegments}, #{version}
)

mapper.update:
UPDATE scenarios
SET version = #{version}, updated_at = NOW()
WHERE scenario_id = #{scenarioId}
